import React, { Component } from "react";
import Card from "../components/Card";
import Alert from "../components/Alert";
import Col from "../components/Col";
import Row from "../components/Row";
import frogs from "../cards.json";

class Home extends Component {
  state = {
    cards: frogs,
    score: 0,
    highScore: 0,
    notification: "Good luck. Ribbit."
  };

  // When the component mounts, load the cards to be displayed
  componentDidMount() {
    this.randomizeCards();
  }

  randomizeCards = () => {
    const currentCards = this.state.frogs.slice();
    let newIndex = this.state.frogs.length;
    let randomIndex = 0;
    let shuffledCards = {};

    // Assign new indices to the cards to shuffle them
    while (newIndex) {
      randomIndex = Math.floor(Math.random() * newIndex--);
      shuffledCards = currentCards[newIndex];
      currentCards[newIndex] = currentCards[randomIndex];
      currentCards[randomIndex] = shuffledCards;
    }
    this.setState({
      frogs: currentCards,
    });
    return frogs;
  }

  // Return score to zero and all cards to a "clicked" value of false.
  resetGame = () => {
    const resetCards = this.state.frogs.map(cards => {
      cards.clicked = false;
      return cards;
    });
    this.setState({
      score: 0,
      frogs: resetCards
    });
  }


  handleBtnClick = (id) => {
    const chosenCard = this.state.frogs.map(chosen => {
      if (chosen.id === id) {
        // If the card has already been clicked...
        if (chosen.clicked === true) {
          // Alert user the card has already been clicked, reset the gaame and shuffle cards
          this.setState({notification: "Oops, you already clicked that one!"});
          this.resetGame();
          this.randomizeCards();
          return chosen;
        }
        this.setState({
          score: this.state.score + 1
        });

        if (this.state.score === this.state.highScore) {
          this.setState({
            highScore: this.state.highScore + 1
          });
        }
        if ((this.state.score + 1) === this.state.frogs.length) {
          this.setState({notification: "You win! Ribbit Ribbit!"});
          this.resetGame();
          this.randomizeCards();
          return chosen;
        } else {
          this.setState({notification: "You guessed correctly!"});
        } 
        console.log(this.state.score);
        console.log(this.state.highScore);
        chosen.clicked = true;
      }
      return chosen;
    })
    this.setState({
      frogs: chosenCard,
    });
    this.randomizeCards();
  }


  handleBtnClick = event => {

    // Get the data-value of the clicked button
    const btnType = event.target.attributes.getNamedItem("data-value").value;
    // Clone this.state to the newState object
    // We'll modify this object and use it to set our component's state
    const newState = { ...this.state };

    if (btnType === "pick") {
      // Set newState.match to either true or false depending on whether or not the card is correct
      newState.match = 1 === Math.floor(Math.random() * 5) + 1;

      // Set newState.score equal to its current value or its current value + 1 depending on whether the card is correct
      newState.score = newState.match
        ? newState.score + 1
        : newState.score;
    } else {
      // If the card is not yet selected, it is marked false
      newState.match = false;
    }
    // Replace our component's state with newState, load the next card image
    this.setState(newState);
    this.loadCards();
  };


  render() {
    return (
      <div>
        <h2 className="text-center">Click on a Card!</h2>
        <h5 className="text-center">
          Click on every image once to earn points. Just don't click on any more than once!
        </h5>
        <Row>
        <Col></Col>
          <Col>
          <Card image={this.state.image} handleBtnClick={this.handleBtnClick} />
          <Card image={this.state.image} handleBtnClick={this.handleBtnClick} />  
          </Col>
          <Col>
          <Card image={this.state.image} handleBtnClick={this.handleBtnClick} />
          <Card image={this.state.image} handleBtnClick={this.handleBtnClick} />  
          </Col>
          <Col>
          <Card image={this.state.image} handleBtnClick={this.handleBtnClick} />
          <Card image={this.state.image} handleBtnClick={this.handleBtnClick} />  
          </Col>
          <Col>
          <Card image={this.state.image} handleBtnClick={this.handleBtnClick} />
          <Card image={this.state.image} handleBtnClick={this.handleBtnClick} />  
          </Col>
        </Row>
        <h1 className="text-center">
         Your Score: {this.state.score}
        </h1>
        <Alert style={{ opacity: this.state.match ? 1 : 0 }} type="success">
          Nice job! You got that one correct!
        </Alert>
      </div>
    );
  }
}

export default Home;
