import React, { Component } from "react";
import Navbar from "./components/Navbar";
import Footer from "./components/Footer";
import Wrapper from "./components/Wrapper";
import Card from "./components/Card";
// import Alert from "../components/Alert";
// import Col from "../components/Col";
// import Row from "../components/Row";
import frogs from "./cards.json";

class App extends Component {
  state = {
    cards: frogs,
    score: 0,
    highScore: 0,
    notification: "Good luck. Ribbit."
  };

  // When the component mounts, load the cards to be displayed
  componentDidMount() {
    this.randomizeCards();
  }

  randomizeCards = () => {
    const currentCards = this.state.frogs.slice();
    let newIndex = this.state.frogs.length;
    let randomIndex = 0;
    let shuffledCards = {};

    // Assign new indices to the cards to shuffle them
    while (newIndex) {
      randomIndex = Math.floor(Math.random() * newIndex--);
      shuffledCards = currentCards[newIndex];
      currentCards[newIndex] = currentCards[randomIndex];
      currentCards[randomIndex] = shuffledCards;
    }
    this.setState({
      frogs: currentCards,
    });
    return frogs;
  }

  // Return score to zero and all cards to a "clicked" value of false.
  resetGame = () => {
    const resetCards = this.state.frogs.map(cards => {
      cards.clicked = false;
      return cards;
    });
    this.setState({
      score: 0,
      frogs: resetCards
    });
  }


  handleBtnClick = (id) => {
    const newState = { ...this.state };
    const chosenCard = newState.frogs.map(chosen => {
      if (chosen.id === id) {
        // If the card has already been clicked...
        if (chosen.clicked === true) {
          // Alert user the card has already been clicked, reset the gaame and shuffle cards
          this.setState({notification: "Oops, you already clicked that one! Croooaak"});
          this.resetGame();
          this.randomizeCards();
          return chosen;
        } 
        //If the card has not been clicked...
        else if (chosen.clicked === false) {
          // Alert user the card has already been clicked, reset the gaame and shuffle cards
          this.setState({notification: "Nice job!"});
          this.randomizeCards();
          return chosen;
        }
        //Increment score by 1
        this.setState({
          score: newState.score + 1
        });

        //Check highscore against current score and update
        if (newState.score >= newState.highScore) {
          this.setState({
            highScore: newState.score
          });
        }

        if ((newState.score + 1) === newState.frogs.length) {
          this.setState({notification: "You win! Ribbit Ribbit!"});
          this.resetGame();
          this.randomizeCards();
          return chosen;
        } 
        chosen.clicked = true;
      }
      return chosen;
    })
    this.setState({
      frogs: chosenCard,
    });
    this.randomizeCards();
  }

  render() {
    return (
        <Wrapper>
          <Navbar
            score={this.state.score}
            highScore={this.state.highScore}
            guess={this.state.notification}
          />
          <div className="card-container">
            {this.state.frogs.map(data => (
              <Card 
                data={data} 
                key={data.id}
                handleBtnClick={this.handleBtnClick}
              />
            ))}
          </div>
          <Footer/>
        </Wrapper>
      );
    //   <Navbar />
    //     <Wrapper>
    //       <div>
    //     <h5 className="text-center">
    //         Click on every image once to earn points. Just don't click on any more than once!
    //       </h5>

    //       <div className="card-container">
    //         {this.state.frogs.map(data => (
    //           <Card
    //             data={data} 
    //             key={data.id}
    //             handleBtnClick={this.handleBtnClick}
    //           />
    //         ))}
    //       </div>
    //       <h1 className="text-center">
    //       Your Score: {this.state.score}
    //       </h1>
    //       <Alert style={{ opacity: this.state.clicked ? 1 : 0 }} type="success">
    //         Nice job! You got that one correct!
    //       </Alert>
    //       </div>
    //     </Wrapper>
    //   <Footer />

    // );
  }
}

export default App;
